sources.sass\\_page-header.scss:
.header {
  padding-top: 30.5px;
  padding-bottom: 50.5px;

  @media screen and (min-width: $tablet) {
    padding-top: 40px;
    padding-bottom: 70px;
  }

  @media screen and (min-width: $desktop) {
    padding-top: 33.5px;
    padding-bottom: 46px;
  }
}

.header__container {
  height: inherit;
}

.header__nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: inherit;
}

.main__logo {
  border: none;
  flex-grow: 1;
}

.logo {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  width: 80px;
  font-weight: 800;
  font-size: 16px;
  line-height: 1.25;
  letter-spacing: 0.04em;
  color: $primary-text-color;
  text-decoration: none;

  @media screen and (min-width: $tablet) {
    width: 130px;
    font-size: 26px;
    line-height: 1.23;
    letter-spacing: 0.04em;
  }
}

.main__icon {
  width: 14px;
  height: 20px;
  margin-left: 2px;
  margin-right: 3px;
  fill: $accent-color;

  @media screen and (min-width: $tablet) {
    width: 23px;
    height: 30px;
    margin-left: 3px;
    margin-right: 3px;
  }
}

.main__logo-rus {
  display: block;
  font-weight: bold;
  font-size: 5px;
  line-height: 2;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  color: $primary-text-color;

  @media screen and (min-width: $tablet) {
    font-size: 8px;
    color: $accent-color;
  }
}

.main__logo-black {
  color: $primary-text-color;
}

.header__list {
  display: flex;
  justify-content: left;
  align-items: center;
  margin: 0;
  padding: 0;
  list-style: none;
  font-weight: bold;
  font-size: 14px;
  line-height: 1.21;
  text-transform: uppercase;

  @media screen and (max-width: 1343px) {
    display: none;
  }

  @media screen and (min-width: $desktop) {
    margin-right: 60px;
  }
}

.header__link {
  text-decoration: none;
  color: $secondary-text-color;
  transition: color 250ms $timing-function;

  &:hover,
  &:focus {
    color: $accent-color;
  }
}

.header__list-item:not(:last-child) {
  margin-right: 40px;
}

.consultation-button {
  height: 48px;
  margin-right: 61px;
  border: 2px solid $accent-color;
  background-color: transparent;
  padding: 0px 11px;
  display: flex;
  font-weight: bold;
  font-size: 12px;
  line-height: 1.25;
  align-items: center;
  text-align: center;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  color: $accent-color;
  cursor: pointer;
  transition: color 250ms $timing-function, background-color 250ms $timing-function;

  &:hover,
  &:focus {
    background-color: $accent-color;
    color: $inverse-text-color;
  }

  @media screen and (max-width: 767px) {
    display: none;
  }

  @media screen and (max-width: 1349px) {
  }

  @media screen and (min-width: $desktop) {
    font-size: 14px;
    line-height: 1.21;
    letter-spacing: 0.14em;
    height: 70px;
    padding: 0px 14px;
  }
}

.menu-toggle {
  position: fixed;
  width: 30px;
  height: 30px;
  top: 31px;
  right: 20px;
  margin-left: 40px;
  background-color: $inverse-text-color;
  border: 1px solid $inverse-text-color;
  border-radius: 4px;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 999;

  @media screen and (min-width: $tablet) {
    top: 49px;
    right: 40px;
  }

  @media screen and (min-width: $desktop) {
    display: none;
  }
}

.menu-toggle__icon {
  width: 21px;
  height: 21px;
  padding-top: 3px;
}

// Мобильное меню

.mobile-head {
  margin-left: auto;
  margin-right: auto;
  max-width: 440px;
  height: 30px;
  padding-right: 3px;
  display: flex;
  justify-content: space-between;
  align-items: center;

  @media screen and (min-width: $tablet) {
    max-width: 688px;
  }
}

.menu-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  padding-top: 30px;
  padding-left: 20px;
  padding-right: 20px;
  background-color: $modal-background-color;
  z-index: 999;
  transform: translate(-100%, -100%);
  transition: transform 250ms $timing-function;

  @media screen and (min-width: $tablet) {
    padding-top: 48px;
    padding-left: 40px;
    padding-right: 40px;
  }

  @media screen and (min-width: $desktop) {
    display: none;
  }
}

.menu-container.is-open {
  transform: translate(0, 0);
}

.logo--inverse,
.main__logo-black--inverse,
.main__logo-rus--inverse,
.header__chapters-item--inverse {
  color: $inverse-text-color;
}

.mobile-menu__list {
  margin-top: 50px;
  margin-bottom: 50px;
  margin-left: auto;
  margin-right: auto;
  max-width: 440px;
  display: flex;
  justify-content: left;

  @media screen and (min-width: $tablet) {
    margin-top: 72px;
    margin-bottom: 0px;
    display: flex;
    justify-content: center;
  }
}

.mobile-menu__chapters {
  padding: 0;
  margin: 0;
  list-style: none;
  font-weight: 800;
  font-size: 20px;
  line-height: 1.8;
  letter-spacing: 0.03em;
  color: $inverse-text-color;

  @media screen and (min-width: $tablet) {
    font-size: 26px;
  }
}

.header__chapters-item--inverse:not(:last-child) {
  margin-bottom: 7px;

  @media screen and (min-width: $tablet) {
    margin-bottom: 10px;
  }
}

.consultation-button__mob-menu {
  display: block;
  margin-left: auto;
  margin-right: auto;

  @media screen and (min-width: $tablet) {
    display: none;
  }
}

.header__icon-cross {
  display: none;
}

.menu-toggle.is-open .header__icon-menu {
  display: none;
}

.menu-toggle.is-open .header__icon-cross {
  display: block;
}

.menu-toggle.is-open {
  z-index: 2000;
  background-color: transparent;
  border: none;
}

.menu-toggle.is-hidden {
  display: none;
}

sources.sass\\_hero.scss:
.hero__shadow {
  padding-bottom: 77px;
  background-size: cover;
  background-repeat: no-repeat;
  background-image: url(../images/hero/gradient-table.png);
  background-position: left -64px bottom 0px;

  @media screen and(min-width:$tablet) {
    background-position: left 0px bottom 0px;
    background-image: url(../images/hero/gradient.png);
  }

  @media screen and(min-width:$desktop) {
    padding-bottom: 107px;
  }
}

// .hero .container {
//   @media screen and(min-width:$tablet) {
//     padding: 0px 18px 0px 40px;
//   }
// }

.hero__position {
  position: relative;
  @media screen and(min-width:$tablet) {
    display: flex;
  }
  @media screen and(min-width:$desktop) {
    padding-bottom: 115px;
  }
}

.hero__container {
  @media screen and(min-width:$mobile-min) {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }
  @media screen and(min-width:$tablet) {
    align-items: flex-start;
  }
}
.hero__title {
  margin: 0px;
  padding: 0px;
  font-style: normal;
  font-weight: 800;
  font-size: 20px;
  margin-bottom: 30px;
  @media screen and(min-width:$tablet) {
    max-width: 332px;
    font-size: 26px;
  }

  @media screen and(min-width:$desktop) {
    max-width: 642px;
    font-size: 58px;
    margin-bottom: 24px;
  }
}

.hero__desc {
  display: flex;
  flex-direction: column;
  margin-bottom: 30px;
  font-style: normal;
  font-size: 14px;
  line-height: 22px;
  margin: 0px;
  padding: 0px;

  @media screen and(min-width:$desktop) {
    font-size: 20px;
  }

  &--container {
    display: flex;
    flex-direction: column;
    @media screen and(min-width:$mobile-min) {
      margin-bottom: 30px;
    }

    @media screen and(min-width:$desktop) {
      flex-direction: row;
      line-height: 164.9%;
      margin-bottom: 40px;
      font-size: 20px;
    }
  }

  &--text {
    font-weight: 500;
    color: $secondary-text-color;
  }

  &--topic {
    font-weight: bold;
    color: $dark-background-color;
  }

  &--label {
    font-weight: bold;
    color: $accent-color;

    @media screen and(min-width:$desktop) {
      margin-left: 5px;
    }
  }
}

.hero__button {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 260px;
  height: 48px;
  margin-bottom: 30px;
  color: $inverse-text-color;
  background-color: $accent-color;
  text-decoration: none;
  text-align: center;
  font-style: normal;
  font-weight: bold;
  font-size: 12px;
  line-height: 15px;
  text-transform: uppercase;
  border: none;

  @media screen and(min-width:$desktop) {
    width: 354px;
    height: 70px;
    padding: 27px 25px 26px 26px;
    margin-bottom: 0px;
    font-size: 14px;
  }
  &:hover,
  :focus {
    border: 2px solid $accent-color;
    background-color: #fff;
    color: $accent-color;
    transition: color 250ms $timing-function, background-color 250ms $timing-function;
  }
}

.hero__thumb {
  width: 210px;
  height: 178px;
  margin-left: auto;
  margin-right: auto;
  @media screen and(min-width:$tablet) {
    position: absolute;
    top: 0px;
    right: 40px;
    width: 376px;
    height: 320px;
  }

  @media screen and(min-width:$desktop) {
    width: 670px;
    height: 572px;
  }
}

.hero__img {
  position: absolute;
  width: 178px;
  top: 0px;
  left: 20px;

  object-position: 67% 50%;
  animation-name: im1-an;
  animation-duration: 4000ms;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  animation-direction: alternate;

  &--opacity {
    position: absolute;
    width: 208px;
    top: 10px;
    left: 0px;

    object-position: 50% 32%;
    opacity: 0;
    animation-name: im2-an;
    animation-duration: 4000ms;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
    animation-direction: alternate;
  }

  @media screen and(min-width:$tablet) {
    width: 321px;
    height: 320px;
    top: 0px;
    right: 19px;
    left: auto;

    &--opacity {
      width: 376px;
      height: 269px;
      top: 17px;
      right: 0px;
      left: auto;
    }
  }

  @media screen and(min-width:$desktop) {
    width: 573px;
    height: 572px;
    top: 0px;
    right: 33px;
    &--opacity {
      width: 670px;
      height: 480px;
      top: 30px;
      right: 0px;
      left: auto;
    }
  }
}

@keyframes im1-an {
  0% {
    opacity: 1;
  }
  30% {
    opacity: 1;
  }
  50% {
    opacity: 0.3;
  }
  70% {
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
}

@keyframes im2-an {
  0% {
    opacity: 0;
  }
  30% {
    opacity: 0;
  }
  50% {
    opacity: 0.7;
  }
  70% {
    opacity: 1;
  }
  100% {
    opacity: 1;
  }
}

