sources.sass\\_problems.scss:
.problems {
  position: relative;
  min-height: 693px;
  padding: 50px 0 0 0;
  color: $inverse-text-color;
  background-image: linear-gradient(
    to bottom,
    $dark-background-color 545px,
    $inverse-text-color 150px
  );

  @media screen and (min-width: $tablet) {
    padding-top: 60px;
    min-height: 495px;
    background-image: linear-gradient(
      to bottom,
      $dark-background-color 345px,
      $inverse-text-color 150px
    );
  }

  @media screen and (min-width: $desktop) {
    min-height: 650px;

    background-image: url(../images/bg_problems.svg),
      linear-gradient(to bottom, $dark-background-color 560px, $inverse-text-color 150px);
    background-repeat: no-repeat;
    background-position: right -50px top 88px, top left;
  }
}

.problems__thumb {
  padding: 20px 30px 20px 30px;
  box-shadow: $box-shadow;
  background-color: $inverse-text-color;

  @media screen and (min-width: $tablet) {
    padding: 35px 95px;
  }

  @media screen and (min-width: $desktop) {
    padding: 65px 113px;
    font-size: 20px;
  }
}

.problems__title {
  margin: 0 0 25px 0;
  padding: 0;
  font-weight: 800;
  font-size: 20px;
  line-height: 130%;
  letter-spacing: 0.03em;
  color: $inverse-text-color;

  &--accent {
    color: $accent-color;
  }

  @media screen and (min-width: $desktop) {
    margin-bottom: 70px;
    font-size: 46px;
  }
}

.problems__list {
  @extend %list;
  counter-reset: items;

  font-weight: 700;
  font-size: 14px;
  line-height: 1.42;
  letter-spacing: 0.08em;
  color: $primary-text-color;

  @media screen and (min-width: $tablet) {
    @include grid(35px);
  }

  @media screen and (min-width: $desktop) {
    font-size: 20px;
  }
}

.problems__item {
  display: flex;
  flex-direction: column;
  margin-bottom: 10px;

  &::before {
    content: '— ' counter(items, decimal-leading-zero);
    counter-increment: items;
    margin-bottom: 5px;

    font-weight: 700;
    font-size: 20px;
    line-height: 164.9%;
    color: $accent-color;
  }

  @media screen and (min-width: $tablet) {
    @include grid-item(35px, 2);
  }

  @media screen and (min-width: $desktop) {
    @include grid-item(35px, 3);
  }
}

sources.sass\\_mixin.scss:
@mixin grid($margin) {
  display: flex;
  flex-wrap: wrap;
  margin-left: -1 * $margin;
  margin-top: -1 * $margin;
}

@mixin grid-item($margin, $qty) {
  flex-basis: calc(100% / #{$qty} - #{$margin});
  margin-left: $margin;
  margin-top: $margin;
}

sources.sass\\_how-we-do.scss:
.how-we-do {
  padding: 50px 0;

  @media screen and (min-width: $desktop) {
    padding: 130px 0;
  }
}

.how-we-do__title {
  margin: 0;
  margin-bottom: 30px;

  font-weight: 800;
  font-size: 20px;
  line-height: 1.3;
  letter-spacing: 0.03em;
  text-align: center;
  color: $primary-text-color;

  @media screen and (min-width: $tablet) {
    font-size: 26px;
  }
  @media screen and (min-width: $desktop) {
    margin-bottom: 50px;
    font-size: 46px;
    line-height: 1.3;
    text-align: left;
  }
}

.how-we-do__list {
  @extend %list;

  @media screen and (min-width: $tablet) {
    @include grid(20px);
    display: flex;
    flex-wrap: wrap;
  }
}

.how-we-do__item {
  margin-bottom: 15px;

  height: 280px;
  background-color: $inverse-text-color;
  box-shadow: $box-shadow;
  cursor: pointer;

  &:last-child {
    margin-bottom: 0;
  }

  @media screen and (min-width: $tablet) {
    height: 334px;
    margin-bottom: 0px;
    @include grid-item(20px, 2);
  }

  @media screen and (min-width: $desktop) {
    height: 396px;
    @include grid-item(20px, 3);
  }

  transition: background-color 250ms $timing-function;

  &:hover,
  &:focus {
    background-color: $accent-color;
  }

  &:hover .how-we-do__icon,
  &:focus .how-we-do__icon {
    fill: $inverse-text-color;
  }

  &:hover .how-we-do__descrition,
  &:focus .how-we-do__descrition {
    color: $inverse-text-color;
  }

  &:hover .how-we-do__descrition::after,
  &:focus .how-we-do__descrition::after {
    background-color: $inverse-text-color;
  }

  &:hover .how-we-do__text,
  &:focus .how-we-do__text {
    color: $inverse-text-color;
  }
}

.how-we-do__thumb {
  display: flex;
  align-items: center;
  justify-content: center;
  padding-top: 30px;
  padding-bottom: 20px;

  @media screen and (min-width: $tablet) {
    padding-top: 40px;
    padding-bottom: 30px;
  }
}

.how-we-do__icon {
  align-items: center;
  fill: $accent-color;
}

.how-we-do__descrition {
  position: relative;
  margin-top: 0;
  margin-bottom: 42px;
  padding-left: 18px;
  padding-right: 13px;

  font-weight: 700;
  font-size: 14px;
  line-height: 1.429;
  text-align: center;
  letter-spacing: 0.08em;
  color: $primary-text-color;

  @media screen and (min-width: $tablet) {
    margin-bottom: 52px;
  }

  @media screen and (min-width: $desktop) {
    margin-bottom: 32px;

    font-size: 20px;
    line-height: 1.4;
  }

  &::after {
    content: ' ';
    position: absolute;
    bottom: -20px;
    width: 33px;
    height: 2px;
    left: 0;
    right: 0;
    margin: 0 auto;
    text-align: center;
    background-color: $accent-color;

    @media screen and (min-width: $tablet) {
      bottom: -25px;
    }

    @media screen and (min-width: $desktop) {
      bottom: -15px;
    }
  }
}

.how-we-do__text {
  margin: 0;
  padding: 0 11px;

  font-weight: 500;
  font-size: 12px;
  line-height: 1.667;
  text-align: center;
  letter-spacing: 0.08em;
  color: $secondary-text-color;

  @media screen and (min-width: $tablet) {
    padding: 0 18px;
  }

  @media screen and (min-width: $desktop) {
    font-size: 18px;
    line-height: 1.556;
  }
}

sources.sass\\_program.scss:
.program {
  counter-reset: semestr;

  overflow: hidden;
  padding-top: 50px;
  padding-bottom: 440px;
  background-color: $dark-background-color;

  @media screen and (min-width: $tablet) {
    padding-top: 60px;
    padding-bottom: 253px;
  }

  @media screen and (min-width: $desktop) {
    padding-top: 120px;
    padding-bottom: 60px;
  }
}

.program__title-wrapper {
  margin-bottom: 43px;

  @media screen and (min-width: $tablet) {
    margin-bottom: 52px;
  }

  @media screen and (min-width: $desktop) {
    margin-bottom: 60px;
    padding-bottom: 100px;

    border-bottom: 1px solid $inverse-text-color;
  }
}

.program__title {
  margin-bottom: 0;
  margin-top: 0;
  text-align: center;
  font-weight: 800;
  font-size: 20px;
  line-height: 1.3;
  letter-spacing: 0.03em;

  color: $inverse-text-color;

  @media screen and (min-width: $tablet) {
    font-size: 26px;
  }

  @media screen and (min-width: $desktop) {
    font-size: 46px;
    line-height: 1.3;
    text-align: left;
  }
}

.program__list {
  @extend %list;

  letter-spacing: 0.08em;
}

.program__list-title {
  position: relative;

  @media screen and (min-width: $desktop) {
    padding-top: 69px;
  }
  // псевдоелемент лічильник
  &::before {
    margin-left: 20px;
    counter-increment: semestr;
    content: counter(semestr) ' ';

    @media screen and (min-width: $tablet) {
      margin-left: 50px;
    }

    @media screen and (min-width: $desktop) {
      margin-left: 0;
    }
  }

  &::after {
    content: '';
    display: inline-block;
    position: absolute;
    left: -5px;
    top: 3px;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background-color: $accent-color;

    @media screen and (min-width: $desktop) {
      left: 0;
      top: 0;
    }
  }
}

.program__list--first {
  margin-left: 68px;

  @media screen and (min-width: $tablet) {
    margin-left: 57px;
  }

  @media screen and (min-width: $desktop) {
    display: flex;
    margin-left: 0;
  }
}

.program__item-first {
  font-weight: bold;
  font-size: 14px;
  line-height: 2;
  text-transform: uppercase;
  color: $inverse-text-color;

  @media screen and (min-width: $desktop) {
    font-size: 20px;
    line-height: 1.4;
    width: 240px;

    &:not(:last-child) {
      margin-right: 30px;
    }
  }
}

.program__list--second {
  padding-top: 17px;
  padding-left: 20px;
  padding-bottom: 52px;
  border-left: 1px solid $inverse-text-color;
  margin-top: -12px;
  margin-bottom: -12px;

  @media screen and (min-width: $tablet) {
    display: flex;
    padding-left: 50px;
    padding-bottom: 42px;
  }

  @media screen and (min-width: $desktop) {
    display: block;
    margin-top: 25px;
    margin-bottom: 0;
    padding-top: 0;
    padding-left: 0;
    padding-bottom: 0;

    border: none;
  }
}

.program__item-second {
  position: relative;
  font-weight: bold;
  font-size: 12px;
  line-height: 1.67;
  text-transform: none;

  color: $accent-color;

  &::before {
    content: '';
    display: inline-block;
    margin-right: 5px;
    margin-bottom: 4px;
    width: 12px;
    height: 1px;

    background-color: $accent-color;
  }

  &:not(:last-child) {
    margin-bottom: 8px;

    @media screen and (min-width: $tablet) {
      margin-right: 50px;
      width: 176px;
      margin-bottom: 0;
    }

    @media screen and (min-width: $desktop) {
      margin-bottom: 25px;
      margin-right: 0;
      width: 100%;
    }
  }

  @media screen and (min-width: $desktop) {
    font-size: 20px;
    width: 100%;
  }
}

.program__list--last {
  margin-bottom: 0;
  padding-bottom: 0;
  border: none;
}

.program__list--third {
  margin-top: 5px;

  @media screen and (min-width: $desktop) {
    margin-top: 10px;
  }
}

.program__item-third {
  font-weight: 500;
  color: $secondary-text-color;
  opacity: 0.5;

