.guarantee__style {
  color: $accent-color;
  border: none;
}

sources.sass\\_reviews.scss:
.home__link {
  @extend %link;

  position: fixed;
  bottom: 20px;
  right: 40px;
  width: 40px;
  height: 40px;
  padding: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: $accent-color;
  background-color: transparent;
  border: 2px solid $accent-color;
  opacity: 0.5;

  transition: color 250ms $timing-function, background-color 250ms $timing-function,
    opacity 250ms $timing-function;

  &:hover {
    opacity: 1;
    background-color: $accent-color;
    color: $inverse-text-color;
  }
}

.home__icon {
  fill: currentColor;
}

.reviews {
  padding-top: 50px;
  padding-bottom: 50px;

  @media screen and (min-width: $desktop) {
    padding-top: 130px;
    padding-bottom: 130px;
  }
}

.reviews__title {
  margin-bottom: 20px;
  margin-top: 0;
  font-weight: 800;
  font-size: 20px;
  line-height: 1.3;
  text-align: center;
  letter-spacing: 0.03em;

  color: $dark-background-color;

  @media screen and (min-width: $tablet) {
    margin-bottom: 30px;
    font-size: 26px;
  }

  @media screen and (min-width: $desktop) {
    margin-bottom: 50px;
    font-size: 46px;
  }
}

.slider__container {
  @media screen and (min-width: $desktop) {
    margin-left: auto;
    margin-right: auto;
    width: 1090px;
  }
}

.review {
  padding: 20px 20px 30px 20px;

  @media screen and (min-width: $tablet) {
    display: flex;
    padding: 40px;

    & > .review__wrapper:first-child {
      margin-right: 46px;
    }
  }

  @media screen and (min-width: $desktop) {
    padding: 50px 110px 50px 80px;
  }
}

.reviews__thumb {
  overflow: hidden;
  border-radius: 50%;
  margin-right: auto;
  margin-left: auto;

  &--slide-for {
    width: 90px;
    height: 90px;
    margin-bottom: 20px;

    @media screen and (min-width: $tablet) {
      width: 150px;
      height: 150px;
    }
  }

  &--slide-nav {
    width: 54px;
    height: 54px;
  }
}

.reviews__img {
  object-fit: cover;
  height: 100%;
  width: 100%;
}

.review__social-link {
  @extend %link;
  cursor: pointer;
  display: flex;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 20px;
  width: 30px;
  height: 30px;
}

.review__title {
  margin-bottom: 10px;
  margin-top: 0;
  font-weight: 800;
  font-size: 14px;
  line-height: 1.2;
  letter-spacing: 0.08em;
  text-align: center;
  color: $accent-color;

  @media screen and (min-width: $tablet) {
    margin-bottom: 20px;
    font-size: 20px;
    text-align: left;
  }

  @media screen and (min-width: $desktop) {
    margin-bottom: 10px;
    font-size: 30px;
  }
}

.review__university {
  margin-bottom: 10px;
  margin-top: 0;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.25;
  letter-spacing: 0.08em;
  text-align: center;
  color: $secondary-text-color;

  @media screen and (min-width: $tablet) {
    text-align: left;
  }

  @media screen and (min-width: $desktop) {
    font-size: 14px;
  }
}

.review__text {
  margin-top: 0;
  margin-bottom: 0;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.7;
  letter-spacing: 0;
  text-align: center;
  color: $slider-text-color;

  @media screen and (min-width: $tablet) {
    text-align: left;
  }

  @media screen and (min-width: $desktop) {
    font-size: 18px;
  }
}
// слайдер
.js .slider-single > div:nth-child(1n + 2) {
  display: none;
}

.js .slider-single.slick-initialized > div:nth-child(1n + 2) {
  display: block;
}

.slider-nav .slick-slide {
  cursor: pointer;
}

.slick-slide.is-active .reviews__thumb {
  border: 2px solid $accent-color;
}

.slider-single {
  margin-bottom: 20px;
  box-shadow: -5px 8px 26px rgba(0, 0, 0, 0.04);
}

.slider-nav {
  margin-right: 50px;
  margin-left: 50px;

  @media screen and (min-width: $tablet) {
    margin-right: 60px;
    margin-left: 60px;
  }
  @media screen and (min-width: $desktop) {
    margin-right: 260px;
    margin-left: 260px;
  }
}

.slick-next {
  right: 0;
  bottom: -59px;
  top: auto;
  width: 25px;
  height: 15px;

  &::before {
    content: '';
    display: block;
    width: 25px;
    height: 15px;
    background-image: url('../images/arrow-right.svg');
    background-size: cover;
    background-position: center;
  }

  @media screen and (min-width: $tablet) {
    right: -13px;
    bottom: auto;
    top: 50%;
  }

  @media screen and (min-width: $desktop) {
    right: -85px;
  }
}

.slick-prev {
  left: 0;
  bottom: -59px;
  top: auto;
  width: 25px;
  height: 15px;

  &::before {
    content: '';
    display: block;
    width: 25px;
    height: 15px;
    background-image: url('../images/arrow-left.svg');
    background-size: cover;
    background-position: center;
  }

  @media screen and (min-width: $tablet) {
    left: -13px;
    bottom: auto;
    top: 50%;
  }

  @media screen and (min-width: $desktop) {
    left: -85px;
  }
}
// Читать дальше
.review__checkbox {
  display: none;
}

.review__text--hidden {
  opacity: 0;
  max-height: 0;
  transform: scale(0.5);
  font-size: 0;
  transition: 250ms $timing-function;
}

.review__checkbox:checked {
  & ~ .review__text .review__text--hidden {
    opacity: 1;
    font-size: inherit;
    max-height: 999em;
  }
}

.review__label::before {
  content: 'Читать дальше';
  text-decoration: underline;
  color: $accent-color;
}

.review__text--visible::after {
  content: '...';
}

.review__checkbox:checked {
  & ~ .review__text .review__label::before {
    content: '';
  }

  & + .review__text .review__text--visible::after {
    content: '';
    margin-left: -3px;
  }
}

.review__label {
  cursor: pointer;
  display: flex;
  justify-content: center;
  font-size: 12px;
  line-height: 1.7;

  @media screen and (min-width: $tablet) {
    display: inline;
  }

  @media screen and (min-width: $desktop) {
    font-size: 18px;
  }
}

sources.sass\\_contacts.scss:
.contacts__title {
  margin: 0px;
  font-weight: 800;
  font-size: 14px;
  line-height: 136%;
  text-align: center;
  color: $inverse-text-color;

  @media (min-width: $tablet) {
    font-size: 26px;
    line-height: 130%;
    letter-spacing: 0.03em;
  }

  @media (min-width: $desktop) {
    font-size: 46px;
  }
}

.contacts__title--accent,
.contacts__text--accent {
  color: $accent-color;
}

.contacts {
  background-image: url('../images/contacts/contacts320.png'),
    linear-gradient(to right, rgba(17, 21, 24, 1), rgba(17, 21, 24, 1));
  background-repeat: no-repeat;
  background-size: cover;
  padding: 50px 0px;
  max-width: 1680px;
  margin-left: auto;
  margin-right: auto;

  @media (min-device-pixel-ratio: 2),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {
    background-image: url('../images/contacts/contacts320@2x.png'),
      linear-gradient(to right, rgba(17, 21, 24, 1), rgba(17, 21, 24, 1));
  }

  @media (min-width: $tablet) {
    padding-bottom: 71px;
    background-image: url('../images/contacts/contacts768.png'),
      linear-gradient(to right, rgba(17, 21, 24, 1), rgba(17, 21, 24, 1));
  }
  @media (min-device-pixel-ratio: 2),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {
    background-image: url('../images/contacts/contacts768@2x.png'),
      linear-gradient(to right, rgba(17, 21, 24, 1), rgba(17, 21, 24, 1));
  }

  @media (min-width: $desktop) {
    padding: 100px 0px 62px 0px;
    background-image: url('../images/contacts/contacts1680.png'),
      linear-gradient(to right, rgba(17, 21, 24, 1), rgba(17, 21, 24, 1));
  }
  @media (min-device-pixel-ratio: 2),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {
    background-image: url('../images/contacts/contacts1680@2x.png'),
      linear-gradient(to right, rgba(17, 21, 24, 1), rgba(17, 21, 24, 1));
  }
}

.contacts__text {
  margin: 0px;
  margin-top: 30px;
  color: $inverse-text-color;
  font-weight: 600;
  font-size: 12px;
  line-height: 15px;
  text-align: center;
  letter-spacing: 0.08em;

  @media (min-width: $desktop) {
    margin-top: 50px;
    font-size: 30px;
    line-height: 37px;
  }
}

.reg-form {
  margin-top: 30px;

  @media (min-width: $tablet) {
    margin-top: 40px;
  }
  @media (min-width: $desktop) {
    margin-top: 56px;
  }
}

.reg-form__label {
  color: $inverse-text-color;
  font-weight: 600;
  line-height: 17px;
  letter-spacing: 0.08em;
  display: block;
  position: relative;
  width: 100%;
  height: 48px;
  margin-left: auto;
  margin-right: auto;

  &:nth-child(2) {
    margin-top: 30px;
    @media (min-width: $desktop) {
      margin-top: 40px;
    }
  }

  @media (min-width: $mobile-min) {
    width: 280px;
  }

  @media (min-width: $tablet) {
    width: 390px;
  }
  @media (min-width: $desktop) {
    width: 478px;
    height: 70px;
    font-size: 20px;
    line-height: 24px;
  }
}

.reg-form__input {
  color: $inverse-text-color;
  background-color: inherit;
  width: 100%;
  height: 100%;
  padding: 0px;
  padding-left: 25px;
  border: 1px solid $inverse-text-color;
  &:invalid {
    border-color: red;
  }
  outline: none;
}

.reg-form__button {
  display: block;
  margin-left: auto;
  margin-right: auto;
  height: 48px;
  width: 260px;
  margin-top: 50px;
  border: 2px solid $accent-color;
  padding: 0px;
  background-color: $accent-color;
  color: $inverse-text-color;
  cursor: pointer;

  font-weight: bold;
  font-size: 12px;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  outline-color: $inverse-text-color;

  transition: background-color 250ms $timing-function, color 250ms $timing-function;

  &:hover,
  &:focus {
    background-color: transparent;
    color: $accent-color;
  }

  @media (min-width: $desktop) {
    width: 354px;
    height: 70px;
    font-size: 14px;
    letter-spacing: 0.14em;
  }
  @media (min-width: $tablet) {
    margin-top: 31px;
  }
  @media (min-width: $desktop) {
    margin-top: 80px;
  }
}

.reg-form__title {
  position: absolute;
  left: 25px;
  top: 15px;
  transform: translateY(0%);
  transition: transform 250ms $timing-function;

  @media (min-width: $desktop) {
    top: 23px;
  }

  .reg-form__input:focus + &,
  .reg-form__input:hover + &,
  .reg-form__input:not(:placeholder-shown) + & {
    transform: translateY(-230%);
  }
}

.br {
  @media (max-width: 767px) {
    display: none;
  }
}

sources.sass\\_page-footer.scss:
.copyright {
  background-color: $dark-background-color;
  height: 70px;
  display: flex;
  align-items: center;
}

.copyright__text {
  margin: 0px;
  color: $secondary-text-color;
  line-height: 28px;
}

.copyright__container {
  width: 100%;
  display: flex;
  justify-content: center;

  @media (min-width: $tablet) {
    justify-content: flex-start;
  }
}

.container--flex {
  display: flex;
  padding-top: 30px;
  padding-bottom: 30px;

  @media (max-width: 767px) {
    flex-direction: column;
    align-items: center;
  }
  @media (min-width: $tablet) {
    padding-top: 40px;
    padding-bottom: 40px;
  }
  @media (min-width: $desktop) {
    padding-top: 53px;
    padding-bottom: 53px;
  }
}

.tel {
  align-items: center;
  display: flex;
  font-weight: 600;
  line-height: 17px;
  letter-spacing: 0.08em;
  color: $dark-background-color;
  text-decoration: none;

  @media (min-width: $tablet) {
    font-size: 20px;
    height: 40px;
  }
  @media (min-width: $desktop) {
    font-size: 30px;
  }
}

.tel__logo {
  margin-right: 10px;
  fill: $accent-color;
  height: 20px;
  width: 20px;

  @media (min-width: $tablet) {
    height: 25px;
    width: 25px;
  }

  @media (min-width: $desktop) {
    height: 30px;
    width: 30px;
  }
}

.instagram {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 40px;
  width: 40px;
  border-radius: 10px;

  background: radial-gradient(
    110.28% 110.28% at 20.5% 87.39%,
    #fae100 0%,
    #fadc04 5.44%,
    #fbce0e 11.67%,
    #fcb720 18.29%,
    #fe9838 25.14%,
    #ff7950 30.54%,
    #ff1c74 49.22%,
    #6c1cd1 100%
  );
}

.instagram__logo {
  max-width: 25px;
  max-height: 25px;
  fill: $inverse-text-color;
}

.footer-contacts {
  margin: 0px;
  padding: 0px;
  display: flex;

  @media (max-width: 767px) {
    flex-direction: column;
    align-items: center;
  }
  @media (min-width: $desktop) {
    flex-grow: 1.4;
  }
}

.footer-contacts__item {
  list-style: none;

  &:first-child {
    @media (max-width: 767px) {
      margin-top: 21px;
    }

    @media (min-width: $tablet) {
      border-right: 1px solid $secondary-text-color;
      padding-right: 24px;
    }
  }

  &:not(:first-child) {
    @media (min-width: $tablet) {
      margin-left: 24px;
    }

    @media (max-width: 767px) {
      margin-top: 31px;
    }
  }
}

